class Del extends React.Component {
  constructor(props) {
    super(props);
    this.state = { username: '', fname: '', in: '', lname: '', street: '', city: '', stateVal: 'UT', zip: '', email: '', password: '', phone: '', redirect: null };
  }
  
  delUser(){
    const data = { userId: this.state.userId, username: this.state.username}
    fetch('http://localhost:9000/test/delUsers', {
      method: 'delete',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
    
    }
    
  

  render() {
    if(this.state.redirect) {
      return <Redirect to={ this.state.redirect }/>
    }
    return(
      <div>
